INSERT INTO gardners_place_encounters VALUES ('A famed escape artist is investigating the area''s legends of an unearthly glow which permeates the area at night. If you give him 2 of your Spells (discarding them), he introduces himself as Erich Weiss; take his Ally card if it''s available. If it''s not available, he shows you a trick instead, and you gain 2 Clue tokens.',NULL,'Miskatonic Horror');
INSERT INTO gardners_place_encounters VALUES ('A gate and a monster appear!',NULL,'Dunwich Horror');
