INSERT INTO gardners_place_encounters VALUES ('A horrifying monster appears!',NULL,'Miskatonic Horror');
INSERT INTO gardners_place_encounters VALUES ('A mutated monster appears! You suffer an extra -1 penalty on any Combat checks against it.',NULL,'Dunwich Horror');
INSERT INTO gardners_place_encounters VALUES ('A weathered man comes up to you as you approach the Gardners'' house. Introducing himself as Ammi Pierce, he tells you that it might be wise to steer clear of the house right now, and asks if you might have anything that might help the family''s strange affliction. If you discard 1 Unique Item, Ammi joins you in gratitude. Take his Ally card if it''s available. If it is not, remove 1 Dunwich Horror token from the board.',NULL,'Dunwich Horror');
