INSERT INTO whateley_farm_encounters VALUES ('Examining one of the haggard cows that shamble around the farm, you realize that something has been draining the creature''s blood on a regular basis. Lose 1 Sanity and move to the street.',NULL,'Dunwich Horror');
INSERT INTO whateley_farm_encounters VALUES ('Examining the remnants of a recent magic ritual, you learn something important about the inner workings of magic. Draw 1 Spell and gain 1 Clue token.',NULL,'Dunwich Horror');
INSERT INTO whateley_farm_encounters VALUES ('Feeling thirsty, you lower a bucket into the old well to draw up a drink. Make a Luck (-1) check. If you pass, the bucket contains several gold coins. Gain $3. If you fail, the bucket is filled with blood and entrails. Lose 1 Sanity and move to the street.','Luck -1','Dunwich Horror');
INSERT INTO whateley_farm_encounters VALUES ('If you pass a Luck (-1) check, you discover some of Wizard Whateley''s notes and find a way to interfere with his plans. You may remove 1 Dunwich Horror token from the board.','Luck -1','Dunwich Horror');
