INSERT INTO jail_cell_encounters VALUES ('Constable Ropes enters your cell with a wooden baton. Some time later, he wearies of his sport and leaves you laying on the floor. Lose 1 Sanity and 1 Stamina. You are delayed, even if you are normally immune to being delayed.',NULL,'Innsmouth Horror');
