INSERT INTO other VALUES ('Dunwich Horror','It seems that you are not the first to have come here. Gain 1 Clue token and $2.',NULL);
INSERT INTO other VALUES ('The Lurker at the Threshold','It takes you a moment to realize that the horrible screeching noise consists of English words spoken by something monstrous! Lose 1 Sanity, but gain 1 Clue token.',NULL);
INSERT INTO other VALUES ('Curse of the Dark Pharaoh','It was a diabolical trap, you suddenly realize... now that you''re inside it! Make a Lore (-1) check or you are delayed.','Lore -1');
