INSERT INTO other VALUES (NULL,'Pass a Luck (-1) check to find a quiet spot to rest. Gain 2 Stamina and 2 Sanity.','Luck -1');
INSERT INTO other VALUES ('Dunwich Horror','Pass a Luck (-1) check to find something of use among the devastation. Draw 1 Common Item.','Luck -1');
INSERT INTO other VALUES (NULL,'Pass a Luck (-1) check to find something to defend yourself with. Take the first Weapon from the Common Item deck.','Luck -1');
INSERT INTO other VALUES (NULL,'Pass a Luck (-1) check to find the gate back. If so, immediately return to Arkham.','Luck -1');
INSERT INTO other VALUES ('Dunwich Horror','Pass a Luck (-2) check to stumble across a valuable item. Take the first Unique Item with a list price of at least $4 from the Unique Item deck.','Luck -2');
INSERT INTO other VALUES (NULL,'Pass a Speed (-1) check to grab the carving before it falls. Draw 1 Spell.','Speed -1');
INSERT INTO other VALUES ('Curse of the Dark Pharaoh','Pass a Speed (-2) check to jump the fissure. If you fail, lose 1 Stamina and you are delayed.','Speed -2');
INSERT INTO other VALUES ('Dunwich Horror','Perhaps there''s something of use here? Pass a Luck (-1) check to either draw 1 Unique Item or draw 1 Common Item and gain $2. If you fail the check, nothing happens.','Luck -1');
