INSERT INTO other VALUES ('Kingsport Horror','Strength- and knowledge?- in numbers. Take a Clue token for every investigator, including you, currently in an Other World.',NULL);
INSERT INTO other VALUES ('Dunwich Horror','Struggling to hold your breath, you cannot seem to find the surface of the mysterious sea. Lose 1 Sanity and 1 Stamina.',NULL);
INSERT INTO other VALUES (NULL,'Suddenly, the things you''ve seen make sense! Gain 1 Clue token.',NULL);
INSERT INTO other VALUES (NULL,'Suddenly you notice something that you had previously missed. Gain 1 Clue token.',NULL);
INSERT INTO other VALUES ('Kingsport Horror','Ten thousand worlds lie arrayed before you; you are delayed.',NULL);
INSERT INTO other VALUES ('The Black Goat of the Woods','The air here is too noxious. You don''t think you can make it. Pass a Speed (-1) check or lose 1 Stamina.','Speed -1');
