INSERT INTO other VALUES ('Dunwich Horror','The creature waves its hand at you. "Blessings upon you." If you have a Curse card, you may discard it.',NULL);
INSERT INTO other VALUES ('Curse of the Dark Pharaoh','The cultists thrust you forward, and the God of the Bloody Tongue surprises you! You gain a +1 to all skill checks against this monster for each Unique Item or Exhibit Item you have.',NULL);
