CREATE TABLE special (
    `list_name` VARCHAR(37) CHARACTER SET utf8,
    `list_expansion` VARCHAR(27) CHARACTER SET utf8,
    `list_type` VARCHAR(14) CHARACTER SET utf8,
    `list_image` VARCHAR(69) CHARACTER SET utf8,
    `list_description` VARCHAR(368) CHARACTER SET utf8
);
INSERT INTO special VALUES ('"One of the Thousand" Cult Membership','The Black Goat of the Woods','Membership','/File%3AOneOfTheThousandCultMembershipBack.png','Whenever you have an encounter in the Black Cave, the Unvisited Isle, or the Woods, instead draw a card from the Cult Encounters deck.');
INSERT INTO special VALUES ('Azure Flame','The King in Yellow','Magical Effect','/File%3AAzureFlame.png','Type: Magical effect
Bonus: +4 to Combat checks
Any phase:  When using the Azure Flame in Combat, all 6''s rolled count as 2 successes.
Discard this card if your Call the Azure Flame is refreshed or leaves your possession.
Hands: 2');
INSERT INTO special VALUES ('Bank Loan',NULL,NULL,'/File%3ABankLoanBack.png','Gain $10 when you take this card.
Upkeep: Roll a die.  On a 1-3, pay $1 or discard all of your items along with this card.  You cannot get another Bank Loan this game.
Any Phase: Pay $10 to pay off your Bank Loan and discard this card.  You may choose to take out a new Bank Loan later on during this game.');
INSERT INTO special VALUES ('Beloved Of Bast','Kingsport Horror',NULL,'/File%3ABelovedOfBastBack.png','When you gain this card, take the Nine Lives card as well.
Upkeep: Gain 1 Sanity or 1 Stamina.
Discard this card if the Ancient One awakens.');
INSERT INTO special VALUES ('Blessing',NULL,NULL,'/File%3ABlessing.png','Upkeep: Roll a die and discard this card on a 1.
When rolling dice, you score successes on a 4, 5, or 6.  If you are Cursed, discard this card instead of gaining a Curse card.');
INSERT INTO special VALUES ('Blood Pact','The Lurker at the Threshold','Dark Pact','/wiki/index.php?title=Special%3AUpload&wpDestFile=LurkerBloodPact.png','When you gain a Blood Pact, restore your Stamina to full.
Any Phase: While this card is refreshed, any time you would gain any amount of Stamina, you may instead gain that amount of Power.
You may spend a Power token as either a Clue token or as 1 Sanity when you suffer a Sanity loss.
Upkeep: Exhaust Blood Pact and lose X Stamina to gain X Power.');
INSERT INTO special VALUES ('Bound Ally','The Lurker at the Threshold','Dark Pact','/wiki/index.php?title=Special%3AUpload&wpDestFile=LurkerBoundAlly.png','When you gain a Bound Ally, take the top card of the Ally deck and attach it to this card.
You gain the services of that Ally. When the Ancient One awakens, discard the Ally and put this card on the Ancient One. For each Bound Ally on the Ancient One, it takes one additional success to remove a doom token.
You may spend a Power token as either a Clue token or as $1.');
INSERT INTO special VALUES ('Captain of the White Ship','Kingsport Horror',NULL,'/File%3ACaptainOfTheWhiteShipBack.png','When you gain this card, take the White Ship as well.
You are never delayed in Other World areas.
Discard this card and the White Ship if you are ever Lost in Time and Space.');
INSERT INTO special VALUES ('Curse',NULL,NULL,'/File%3ACurse.png','Upkeep: Roll a die and discard this card on a 1.
Any Phase: When rolling dice, you only score successes on a 6.  If you are Blessed, discard this card instead of gaining a Blessing card.');
INSERT INTO special VALUES ('Changed','Kingsport Horror',NULL,'/File%3AChangedBack.png','When you gain this card, take the Great Seal as well.
+1 Maximum Sanity
+1 Maximum Stamina
Any Phase: Exhaust instead of spending a Clue token.');
INSERT INTO special VALUES ('Coded Messages','Dunwich Horror','Condition','/File%3ACodedMessagesBack.png','Condition: Investigators may trade Clue tokens with other investigators in the same area during the Movement phase as though Clue tokens were items.');
INSERT INTO special VALUES ('Darke''s Blessing','Dunwich Horror','Condition','/File%3ADarkesBlessingBack.png','Condition - Any Phase: Any investigator may exhaust this card to re-roll a skill check that he or she has just failed.  This card refreshes at the start of each Upkeep phase.');
INSERT INTO special VALUES ('Deputy of Arkham',NULL,NULL,'/File%3ADeputyOfArkhamBack.png','When you gain this card, take the Deputy''s Revolver and the Patrol Wagon as well.
Upkeep: Gain $1');
INSERT INTO special VALUES ('Deputy''s Revolver',NULL,NULL,'/File%3ADeputyOfArkhamBack.png','Type: Physical Weapon
Bonus: +3 Combat check
Deputy''s Revolver cannot be lost or stolen unless you choose to allow it.
Hands: 1');
INSERT INTO special VALUES ('Inner Beast','The King in Yellow','Magical Effect','/File%3AInnerBeast.png','Type: Magical effect
Bonus:
+1 Maximum Stamina
-2 Maximum Sanity
+2 to Combat checks
Movement:  You receive 1 extra movement point.
Discard this card if your Summon the Beast Within is refreshed or leaves your possession.');
INSERT INTO special VALUES ('Nine Lives','Kingsport Horror',NULL,'/File%3ABelovedOfBastBack.png','While you have this card, you cannot be devoured unless you choose to allow it.
Discard this card if the Ancient One awakens.');
INSERT INTO special VALUES ('Patrol Wagon',NULL,NULL,'/File%3ADeputyOfArkhamBack.png','Movement:  If you are in Arkham, you may move to any street area or location in Arkham instead of your normal movement.
Roll a die at the end of each Combat and whenever you return to Arkham from an Other World. On a 1, return this card to the box.
');
INSERT INTO special VALUES ('Rare Book Collection','Dunwich Horror','Condition','/File%3ARareBookCollectionBack.png','Condition: The Library has the following special ability:
Rare Book Collection:  Instead of having an encounter here, you may lose 1 Sanity to gain 1 Clue token.');
INSERT INTO special VALUES ('Rail Pass','Dunwich Horror',NULL,'/File%3ARailPassBack.png','You do not have to pay $1 to travel between towns (by rail).');
INSERT INTO special VALUES ('Retainer',NULL,NULL,'/File%3ARetainer.png','Upkeep: Gain $2, then roll a die.  Discard this card on a 1.');
INSERT INTO special VALUES ('Sheldon Gang Membership','Dunwich Horror','Membership','/File%3ASheldonGangMembershipBack.png','Upkeep: If you are in a street area, you may roll a die and consult the chart below to engage in some petty larceny.');
INSERT INTO special VALUES ('Silver Twilight Lodge Membership',NULL,'Membership','/File%3ASilverTwilightLodgeMembershipBack.png','Any Phase: Whenever you have an encounter at the Silver Twilight Lodge, you have an encounter at the Inner Sanctum instead.');
INSERT INTO special VALUES ('Soul Pact','The Lurker at the Threshold','Dark Pact','/wiki/index.php?title=Special%3AUpload&wpDestFile=LurkerSoulPact.png','When you gain a Soul Pact, restore your Sanity to full.
Any Phase: While this card is refreshed, anytime you would gain any amount of Sanity, you may instead gain that amount of Power.
You may spend a Power token as either a Clue token or as 1 Stamina when you suffer a Stamina loss.
Upkeep: Exhaust Soul Pact and lose X Sanity to gain X Power.');
INSERT INTO special VALUES ('The Great Seal','Kingsport Horror',NULL,'/File%3AChangedBack.png','Any Phase: When you seal a gate, you may place this card on the sealed location along with the Elder Sign token.  That elder sign token cannot be removed by any game effect, including gate bursts.');
INSERT INTO special VALUES ('The White Ship','Kingsport Horror',NULL,'/File%3ACaptainOfTheWhiteShipBack.png','Movement:  If you are in Arkham or an Other World, you may move to the first area of any Other World instead of your normal movement.  You may do this even if there are no open gates leading to that Other World.');
INSERT INTO special VALUES ('Third Eye','The King in Yellow','Magical Effect','/File%3AThirdEye.png','Type: Magical effect
Bonus:
+1 Maximum Sanity
-2 Maximum Stamina
+2 to Horror checks
You may not be Cursed.  (If you are already Cursed, this card does not change that.)
Discard this card if your Open the Third Eye is refreshed or leaves your possession.');
INSERT INTO special VALUES ('Velma''s Gratitude','Dunwich Horror','Condition','/File%3AVelmasGratitudeBack.png','Condition: Investigators may ignore any money losses caused by Velma''s Diner encounters and are always considered to have spent the maximum amount of money allowed during encounters there.');
