CREATE TABLE injury (
    `list_name` VARCHAR(19) CHARACTER SET utf8,
    `list_expansion` VARCHAR(17) CHARACTER SET utf8,
    `list_count` INT,
    `list_matching_injury` VARCHAR(17) CHARACTER SET utf8,
    `list_description` VARCHAR(262) CHARACTER SET utf8,
    `list_image` VARCHAR(25) CHARACTER SET utf8
);
INSERT INTO injury VALUES ('Allergic Reactions','Miskatonic Horror',1,'Agoraphobia','Upkeep: Lose 1 Stamina if you are in a street area.
